mapper.update:
update student s set s.violation_score= s.violation_score+#{score} ,
                     s.violation_count = s.violation_count + 1
where id = #{id}

mapper.select:
select s.*, c.name clazzName from student s left join clazz c on s.clazz_id = c.id


        AND s.name LIKE concat('%', #{name}, '%')


        AND s.degree = #{degree}


        AND s.clazz_id = #{clazzId}


ORDER BY s.update_time DESC
